style:
body { font-family: Arial, sans-serif; font-size: 12px; line-height: 1.4; }
.print-format { max-width: 800px; margin: 0 auto; padding: 20px; }
.print-heading { text-align: center; margin-bottom: 30px; border-bottom: 2px solid #333; padding-bottom: 20px; }
.print-heading h1 { color: #333; margin-bottom: 20px; }
.print-heading-row { display: flex; justify-content: space-between; margin-bottom: 5px; }
.print-heading-label { font-weight: bold; }
.section { margin-bottom: 30px; }
.section h2 { color: #333; border-bottom: 1px solid #ccc; padding-bottom: 5px; margin-bottom: 15px; }
.row { display: flex; margin-bottom: 10px; }
.col-6 { flex: 1; }
.table { width: 100%; border-collapse: collapse; margin: 15px 0; }
.table th, .table td { border: 1px solid #ddd; padding: 8px; text-align: left; }
.table th { background-color: #f5f5f5; font-weight: bold; }
.notes { background-color: #f9f9f9; padding: 15px; border-left: 3px solid #333; }
.footer { margin-top: 40px; padding-top: 20px; border-top: 1px solid #ccc; text-align: center; font-size: 10px; color: #666; }
